#include <iostream>
#include <sstream>
#include <vector>
#include <string>
using namespace std;
vector<string> splitString(const string& str, const string& delimiter) {
    vector<string> tokens;
    string token;
    stringstream ss(str);

    while (getline(ss, token, delimiter[0])) { // Assuming single character as a delimiter
        tokens.push_back(token);
    }

    return tokens;
}

int main() {
    string myString = "part1,part2,part3";
    string delimiter = ",";

    vector<string> parts = splitString(myString, delimiter);

    for (const auto& part : parts) {
        cout << part << endl;
    }

    return 0;
}
