#include "UTMS.hpp"
#include "Orders.hpp"

int main(int argc, char *argv[])
{
    if (argc != 5)
    {
        cout << ERROR << endl;
        return 0;
    }
    vector<string> pathes;

    for (int i = 1; i < 5; i++)
    {
        pathes.push_back(argv[i]);
    }
    UTMS *utms = new UTMS(pathes);
    Orders *orders = new Orders(utms);
    orders->input();
    delete orders;
    delete utms;
    return 1;
}

#include "../server/server.hpp"
#include "handlers.hpp"

void mapServerPaths(Server &server)
{
    server.setNotFoundErrPage("static/404.html");
    server.get("/", new ShowPage("static/home.html"));
    server.get("/home.png", new ShowImage("static/home.png"));
    server.get("/rand", new RandomNumberHandler());
    server.get("/login", new ShowPage("static/logincss.html"));
    server.post("/login", new LoginHandler());
    server.get("/up", new ShowPage("static/upload_form.html"));
    server.post("/up", new UploadHandler());
    server.get("/colors", new ColorHandler("template/colors.html"));
    server.get("/music", new ShowPage("static/music.html"));
    server.get("/music/moonlight.mp3", new ShowFile("static/moonlight.mp3", "audio/mpeg"));
}

int main(int argc, char **argv)
{

        int port = argc > 1 ? stoi(argv[1]) : 5000;
        Server server(port);
        mapServerPaths(server);
        cout << "Server running on port: " << port << endl;
        server.run();


    return 0;
}
