{
    if(stoi(id)<=0)
        throw runtime_error(BAD_REQUEST_ERROR);

    for (int i = 0; i < posts.size(); i++)
    {
        if (stoi(id) == posts[i].id)
        {
            posts.erase(posts.begin() + i);
            cout << OK << endl;
            return;
        }
    }
    throw runtime_error(NOT_FOUND_ERROR);
}

