void UTMS::handlePOST(string line, vector<string> apartedBySpace)
{
    map<string, string> params;
    for (int i = 3; i < apartedBySpace.size(); i += 2)
    {
        params[apartedBySpace[i]] = apartedBySpace[i + 1];
    }

    try
    {
        if (apartedBySpace[1] == ACTION_LOGIN)
        {
            if (params.size() != (CHECK_CORRECT_LENGTH_LOGIN - 2) / 2)
            {
                throw runtime_error(ERROR_BAD_REQUEST);
            }
            if (params.count(ID_CHECK) && params.count(PASSWORD_CHECK))
            {
                POSTlogin(params[ID_CHECK], params[PASSWORD_CHECK]);
            }
            else
            {
                throw runtime_error(ERROR_NOT_FOUND);
            }
        }
    }
    catch (runtime_error &e)
    {
        cout << e.what() << endl;
    }
