#include <iostream>
#include <sstream>
#include <vector>
#include <string>
using namespace std;
std::vector<std::string> splitString(const std::string& str, const std::string& delimiter) {
    std::vector<std::string> tokens;
    std::string token;
    std::stringstream ss(str);

    while (std::getline(ss, token, delimiter[0])) { // Assuming single character as a delimiter
        tokens.push_back(token);
    }

    return tokens;
}

int main() {
    std::string myString = "part1,part2,part3";
    std::string delimiter = ",";

    std::vector<std::string> parts = splitString(myString, delimiter);

    for (const auto& part : parts) {
        std::cout << part << std::endl;
    }

    return 0;
}
